document.addEventListener('DOMContentLoaded', function () {
    var swiper = new Swiper(".block-review__items", {
        loop: true,
        pagination: {
            el: ".swiper-pagination",
            clickable: true,
        },
        navigation: {
            nextEl: ".swiper-button-next",
            prevEl: ".swiper-button-prev",
        },
    });
});
